import { Axios } from "axios";
import React, { Component, createRef, useEffect, useState } from "react";

// **** React Hooks ****
const NobdList = () => { // 함수는 대문사 명시 필요.
    const [count, setCount] = useState(0);
    const [email, setEmail] = useState("");
    const updateEmail = e => {
        const {
            target : {value}
        } = e;
        setEmail(value);
    }
    const onClickOutside = () => {
        console.log("serim");
    }
    const ref = ClickOutside(onClickOutside);
    const name = useInput("");

    return (
        <>
            <div ref={ref}>
                <h1>List</h1>
                <h2>popup</h2>

                <input {...name} placeholder="what your name" />
            </div>
            
            <p>{count}</p>
            <hr/>
            <button onClick={() => setCount(count+1)}>추천</button>
            <button onClick={() => setCount(count-1)}>비추천</button>
            <input placeholder="Email" value={email} onChange={updateEmail} />
        </>
    );
};

function ClickOutside (fn) {
    const ref = createRef();
    const handleClick = e => {
        //console.log(ref.current.contains(e.target));
        if(!ref.current.contains(e.target)){
            //console.log("clicked outside");
            fn();
        }
    };

    useEffect(() => {
        document.addEventListener("click", handleClick);
    }, [])

    return ref;
}

function useInput(defaultValue){
    const [value, setValue] = useState(defaultValue);

    const onChange = e => {
        const {
            target: {value}
        } = e;
        setValue(value);
    };

    return {value, onChange};
}

function usefetch(url) {
    const [payload, setPayload] = useSate(null);
    const [loading, setLoading] = userState(true);
    const [error, setError] = userState("");

    const callUrl = async () => {
        try {
            const {data} = await Axios.get(url);
            setPayload(data);
        } catch (error) {
            setError("error!");
        } finally {
            setLoading(false);
        }
    };

    return {payload, loading, error}
}

export default NobdList;

