mapper.select:
SELECT useremail , username , userpassword , userphone , userflag , usermajor , userorg , reg_user , reg_date , update_user , update_date FROM react.react_user
WHERE useremail = #{is_useremail}
ORDER BY username

mapper.update:

UPDATE react.react_user
SET
  username = #{name_val}
  , userphone = concat(#{phone1_val},'-',#{phone2_val},'-',#{phone3_val})
  , usermajor = #{major_val}
  , userorg = #{org_val}
  , update_date = DATE_FORMAT(now(), '%Y%m%d%H%i%s')
WHERE useremail = concat(#{email_val},'@', #{email2_val})
